#include "mpiDatatype.h"
template <> MPI_Datatype mpiDatatype<char>()     { return MPI_CHAR;     } template <> MPI_Datatype mpiDatatype<uint8_t>()  { return MPI_UINT8_T;  } template <> MPI_Datatype mpiDatatype<uint16_t>() { return MPI_UINT16_T; } template <> MPI_Datatype mpiDatatype<int32_t>()  { return MPI_INT32_T;  } template <> MPI_Datatype mpiDatatype<uint32_t>() { return MPI_UINT32_T; } template <> MPI_Datatype mpiDatatype<int64_t>()  { return MPI_INT64_T;  } template <> MPI_Datatype mpiDatatype<uint64_t>() { return MPI_UINT64_T; } template <> MPI_Datatype mpiDatatype<float>()    { return MPI_FLOAT;    } template <> MPI_Datatype mpiDatatype<double>()   { return MPI_DOUBLE;   }
